  `id_kategori_cerbung` int(20) NOT NULL,
  `foto_cerbung` text NOT NULL,
  `usernames` varchar(30) NOT NULL,
  `id_users` int(11) NOT NULL,
  `headline_cerbung` varchar(1) NOT NULL,
  `status_cerbung` varchar(1) NOT NULL,
